const initialState = {
  cartProduct: []
}
const addToCartReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      return {
        ...state,
        cartProduct: [...state, action.payload]
      }
      default:
        return state;
  }
}

export default addToCartReducer;
